/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode(int x) : val(x), next(NULL) {}
 * };
 */
class Solution {
public:
    ListNode* reverseList(ListNode* head) {

        ListNode* next = nullptr;
        ListNode* pre = nullptr;
        ListNode* current = head;
        while(current){
            next = current->next; 
            current->next = pre;
            pre = current;
            current = next;
        }
        return pre;
    }
};

