143. Reorder List
143. Reorder List
/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode(int x) : val(x), next(NULL) {}
 * };
 */
class Solution {
public:
    void reorderList(ListNode* head) {

        if(head == NULL) return;


        ListNode* slow = head;
        ListNode* fast = head;

        // 找出 middle
        while(fast != NULL && fast->next != NULL){
            slow = slow->next;
            fast = fast->next->next;
        }

        ListNode* l1 = head;
        ListNode* l2 = slow->next; // 這裡有點奇怪
        slow->next = NULL;

        l2 = reverse(l2);

        // 合併

        while(l1 != NULL && l2 != NULL){
            ListNode* next = l1->next;
            l1->next = l2;
            l2 = l2->next;
            l1->next->next = next;
            l1= next;
       }
    }
private:
    ListNode* reverse(ListNode* head){
        ListNode* next = nullptr;
        ListNode* pre = nullptr;
        ListNode* current = head;
        while(current){
            next = current->next; 
            current->next = pre;
            pre = current;
            current = next;
        }
        return pre;
    }
};

61. Rotate List
61. Rotate List
時間複雜度: O(n)
/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode(int x) : val(x), next(NULL) {}
 * };
 */
class Solution {
public:
    ListNode* rotateRight(ListNode* head, int k) {

        if(k == 0 || head == NULL){
            return head;
        }

        // 先計算 list 的長度
        int size = 0;
        ListNode* last = head;
        while(last->next != NULL){
            last = last->next;
            size++;
        }
        size++;

        // 計算要翻轉的位置
        k = k % size;

        // 如果 k % size == 0 則直接回傳 head
        // ex. 1->2->3 and k == 3  ( k % size = 0 )
        // 1. 3->1->2
        // 2. 2->3->1
        // 3. 1->2->3
        if(k == 0){
            return head;
        }

        int target = size - k - 1;
        // 開始翻轉
        // ex. 1->2->3- and k = 1 ( k % size = 1 )
        // 移動到 size - (k % size) -1 == 1 這個位置，
        // 它就 rotate 後的最後一個位置。

        ListNode* cur = head;

        while(target != 0){
            cur = cur->next;
            target--;
        }

        ListNode* res = cur->next;
        cur->next = NULL;
        last->next = head;

        return res;
    }
};

