class Solution {
public:
    Node* cloneGraph(Node* node) {
        if(node == NULL){
            return node;
        }

        queue queue;

        // 用來存放  節點的對應關係
        unordered_map map;

        queue.push(node);
        Node* clone = new Node(node->val);
        map[node] = clone;

        // bfs
        while(!queue.empty()){
            Node* cur = queue.front();
            queue.pop();

            for (auto neighbor : cur->neighbors){
                if(!map.count(neighbor)){
                    map[neighbor] = new Node(neighbor->val);
                    queue.push(neighbor);
                }
                map[cur]->neighbors.push_back(map[neighbor]);
            }
        }
        return clone;
    }
};

