102. Binary Tree Level Order Traversal

/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode(int x) : val(x), left(NULL), right(NULL) {}
 * };
 */
class Solution {
public:
    vector> levelOrder(TreeNode* root) {

        vector> res;
        if(root == NULL){
            return res;
        }

        vector subres;
        queue queue;
        queue.push(root);

        while(!queue.empty()){
            int size = queue.size();
            vector subres;

            while(size--){
                TreeNode* cur = queue.front();
                queue.pop();
                subres.push_back(cur->val);

                if(cur->left){
                    queue.push(cur->left);
                }

                if(cur->right){
                    queue.push(cur->right);
                }
            }
            res.push_back(subres);
        }

        return res;
    }
};

