class Solution {
public:
    vector> permuteUnique(vector& nums) {

        vector> res;
        if(nums.size() == 0){
            return res;
        }

        sort(nums.begin(), nums.end());
        vector subres;
        vector visited(nums.size(), 0);

        dfsHelper(res, subres, nums, visited, 0);

        return res;
    }
private:
    void dfsHelper(
        vector>& res,
        vector& subres,
        vector& nums,
        vector& visited,
        int start
    ){
        if(nums.size() == subres.size()){
            res.push_back(subres);
            return;
        }

        for (int i=start; i  0 && nums[i] == nums[i-1] && visited[i-1] == false )){
                continue;
            }

            visited[i] = true;
            subres.push_back(nums[i]);
            dfsHelper(res, subres, nums, visited, start);
            subres.pop_back();
            visited[i] = false;
        }
    }
};

