class Solution {
public:
    Node* connect(Node* root) {
        if(!root){
            return root;
        }

        queue q;
        q.push(root);
        while(!q.empty()){
            int size = q.size();
            while(size--){
                Node* cur = q.front();
                q.pop();

                if(size != 0){
                    cur->next = q.front();
                }

                if(cur->left != NULL){
                    q.push(cur->left);
                }

                if(cur->right != NULL){
                    q.push(cur->right);
                }
            }
        }
        return root;
    }
};

