                    for (int b: rightRes){
                        res.push_back(f(a,b));
                    }
                }
            }
        }

        // 如果 res 為空，則代表為單個數值 ex: input: '123' or '1'
        if(res.empty()){
            res.push_back(std::stoi(input));
        }

        return cache[input] = res;
    };
    unordered_map> cache;
public:
    vector diffWaysToCompute(string input) {
        return ways(input);
    }
};

