450. Delete Node in a BST
450. Delete Node in a BST
/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode(int x) : val(x), left(NULL), right(NULL) {}
 * };
 */
class Solution {
public:
    // def: 將一個 target 給移除，並且回傳一個已移除目標的 tree
    // return: 已移除 target 的 tree
    TreeNode* deleteNode(TreeNode* root, int key) {

        if(!root){
            return root;
        }

        if(key > root->val){
            root->right = deleteNode(root->right, key);
        }else if(key val){
            root->left = deleteNode(root->left, key);
        }else{

            // 情況1: 左右子樹皆為 null => 則直接回傳 null
            if(!root->right && !root->left){
                return nullptr;
            }

            // 情況2: 左子樹為空，右子樹有節點 => 則直接回傳右子樹
            if(!root->left && root->right){
                return root->right; 
            }

            // 情況3: 左子樹為空，左子樹有節點 => 則直接回傳左子樹
            if(!root->right && root->left){
                return root->left;
            }

            // 情況4: 左右子樹皆有值 
            // => 需尋找到右子樹的最小值，然後在重建一個已移除最小值的右子樹
            // => 升級為節點的條件為: 
            // 1. 右子樹優先
            // 2. 右子樹還有左右子樹，則子樹的左子樹為優先
            if(root->right && root->left){
                TreeNode* target = root->right;
                while(target->left){
                    target = target->left;
                }
                root->val = target->val;
                root->right = deleteNode(root->right, target->val);
            }
        }
        return root;
    }
};

