class Solution {
public:
    // def: 倒置一顆 tree
    // return: 回傳已倒置後的 tree
    TreeNode* invertTree(TreeNode* root) {
        if(!root){
            return root;
        }

        // tip: 這裡需要一個 temp 來儲放 left，因為在 21 行時，left 會被改變。
        TreeNode* temp = root->left;
        root->left = invertTree(root->right);
        root->right = invertTree(temp);

        return root;
    }
};

