/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode(int x) : val(x), left(NULL), right(NULL) {}
 * };
 */
class Solution {
public:
    TreeNode* invertTree(TreeNode* root) {
        stack s;
        s.push(root);

        while(!s.empty()){
            TreeNode* cur = s.top();
            s.pop();
            if(cur){
                s.push(cur->left);
                s.push(cur->right);
                swap(cur->left, cur->right);
            }
        }
        return root;
    }
};

