/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode(int x) : val(x), left(NULL), right(NULL) {}
 * };
 */
class Solution {
public:
    bool isSymmetric(TreeNode* root) {
        if(!root){
            return true;
        }

        queue q1,q2;
        q1.push(root->left);
        q2.push(root->right);

        while(!q1.empty() && !q2.empty()){
            TreeNode* p = q1.front();
            q1.pop();
            TreeNode* q = q2.front();
            q2.pop();

            if(!p && !q){
                continue;
            }

            if(!p || !q){
                return false;
            }

            if(p->val != q->val){
                return false;
            }

            q1.push(p->left);
            q2.push(q->right);
            q1.push(p->right);
            q2.push(q->left);
        }

        return true;
    }
};

