/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode(int x) : val(x), left(NULL), right(NULL) {}
 * };
 */
class Solution {
public:
    bool isValidBST(TreeNode* root) {
        if(!root){
            return true;
        }

        return isValidBST(root, NULL, NULL);
    }

    bool isValidBST(TreeNode* root, TreeNode* minNode, TreeNode* maxNode){
        if(!root) return true;

        // 情況1: 如果 minNode 存在，而且當前節點小於它，則回傳 false (右子樹的比較)
        if(minNode && root->val val){
            return false;
        }

        // 情況2: 如果 maxNode 存在，而當前節點大於它，則回傳 false (左子樹的比較)
        if(maxNode && root->val >= maxNode->val){
            return false;
        }

        return isValidBST(root->left, minNode, root) && isValidBST(root->right, root, maxNode);
    }
};

Inorder (iteration)
/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode(int x) : val(x), left(NULL), right(NULL) {}
 * };
 */
class Solution {
public:
    bool isValidBST(TreeNode* root) {
        // inorder => 左根右
        if(!root){
            return true;
        }

        stack s;
        TreeNode* cur = root;
        TreeNode* pre = NULL;

        while(cur || !s.empty()){
            while(cur){
                s.push(cur);
                cur = cur->left;
            }

            cur = s.top();
            s.pop();

            // 情況，如果 pre 大於 cur => 不是 bsf
            // tip: 因為如果是 bst 用 inorder，會是由小走到大
            if(pre && pre->val >= cur->val){
                return false;
            }

            pre = cur;
            cur = cur->right;
        }
        return true;
    }
};

110. Balanced Binary Tree
110. Balanced Binary Tree
/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode(int x) : val(x), left(NULL), right(NULL) {}
 * };
 */
class Solution {
public:
    bool isBalanced(TreeNode* root) {

        if(!root){
            return true;
        }

        // 是 balanced 的條件為:
        // 1. 左右子樹的深度不超過 1
        // 2. 左子樹與右子樹皆為 balanced tree。
        // 注意: 需要第二個條件，不然下列 tree 會錯
        //          A
        //        B   B
        //       C     C
        //      D       D

        return abs(depth(root->right) - depth(root->left)) left) 
            && isBalanced(root->right); 
    }
private:
    int depth(TreeNode* root){
        if(!root){
            return 0;
        }
        return max(depth(root->left), depth(root->right)) + 1;
    }
};

