class LRUCache {
private:
    struct Node {
        int key;
        int value;
        Node* next;
        Node* prev;
        Node(int key, int value): key(key),value(vale),next(NULL),prev(NULL) {};
    };
    int capacity_;
    int size_;
    unordered_map map;
    Node* head;
    Node* tail;

    void insertToFront(int key, int value){
        Node* node = new Node(key, value);
        map[key] = node;

        if(size_++ == 0){
            head = tail = node;
            return;
        }

        head->prev = node;
        node->next = head;
        head = head->prev;
    };

    void moveToFront(int key){
        if(map.count(key) == 0 || map[key] == head) return;
        Node* node = map[key];

        if(node == tail){
            node->prev->next = NULL;
            tail = node->prev;
        }else{
            node->prev->next = node->next;
            node->next->prev = node->prev;
        }

        head->prev = node;
        node->next = head;
        node->prev = NULL;
        head = head->prev;
    }

    void removeTail(){
        if(!tail) return;
        map.erase(tail->key);

        if(head == tail){
            head = tail = NULL;
        }else{
            tail = tail->prev;
            tail->next = NULL;
        }

        size_--;
    }

public:
    LRUCache(int capacity) {
       capacity_ = capacity; 
       size_ = 0;
       Node* head = NULL;
       Node* tail = NULL;
    }

    int get(int key) {

        if(map.count(key) == 0){
            return -1;
        }

        // 2. 如果有此 key
        // => 則回傳此 key，前將它移至最前面
        moveToFront(key);

        return map[key]->value;
    }

    void put(int key, int value) {
        // 1. 如果沒有此 key 且 capacity 還沒達到上限
        // => 則新增至 lru 最前面
        if(map.count(key) == 0 && size_  則將此 key 移至 lru 最前面
        if(get(key) != -1){
            map[key]->value = value;
            return;
        }

        // 3. 如果沒有 key 且 capacity 已達上限
        // => 則將此 key 新增至 lru 最前面
        // => 且已除最後一個舊的資料
        if(map.count(key) == 0 && size_ == capacity_){
            removeTail();
            insertToFront(key, value);
        }
    }
};

