Memory Usage: 22.3 MB, less than 27.08% of C++ online submissions for Insert Delete GetRandom O(1).
class RandomizedSet {
private:
    unordered_map map;
    vector datas;
    int size_;
public:
    /** Initialize your data structure here. */
    RandomizedSet() {
        size_ = 0;
    }

    /** Inserts a value to the set. Returns true if the set did not already contain the specified element. */
    bool insert(int val) {
        if(map.count(val)){
            return false;
        }

        datas.push_back(val);
        // 將 val 丟到 hash 中，並且 hash 的 value 要存放放在陣列的位置
        map[val] = size_;
        size_++;

        return true;
    }

    /** Removes a value from the set. Returns true if the set contained the specified element. */
    bool remove(int val) {

        if(!map.count(val)){
            return false;
        }
        // 從 map 中找出 val 的 datas 位置在從此處將它移除
        int index = map[val];

        // vector 刪除不是 o(1)，因此需要進行以下的操作
        // swap 目標和最後一個數 o(1)
        // 在刪除 vector 最後一個數 o(1)
        int last = datas[size_ - 1];
        map[last] = map[val];
        datas[index] = last;
        datas.pop_back();
        map.erase(val);

        size_--;

        return true;
    }

    /** Get a random element from the set. */
    int getRandom() {
        return datas[rand() % size_];
    }
};

