Memory Usage: 18.7 MB, less than 11.17% of C++ online submissions for Generate Parentheses.

class Solution {
private:
    // def: dfs 往 sub 往下建立 Parentheses
    // l => 左括號可用數
    // r => 右括號可用數
    void dfsHelper(
        vector& res,
        string sub,
        int l,
        int r
    ){
        if(l == 0 && r == 0){
            res.push_back(sub);
            return;
        }

        if(l = r) return;
        dfsHelper(res, sub + ")", l, r-1);
    }
public:
    vector generateParenthesis(int n) {
        vector res;
        dfsHelper(res, "", n, n);
        return res;
    }
};

