class Trie {
private:
    struct TrieNode{
        char text;
        bool isWord;
        unordered_map map;
        TrieNode(){
            isWord = false;
        }
    };
    TrieNode* root;
public:
    /** Initialize your data structure here. */
    Trie() {
        root = new TrieNode();
    }

    /** Inserts a word into the trie. */
    void insert(string word) {
        TrieNode* cur = root;
        for (int i=0; i map[word[i]]; 
            if(next == nullptr){
                next = new TrieNode();
                next->text = word[i];
            }

            if(i == word.size() - 1){
                next->isWord = true;
            }
            cur->map[word[i]] = next;
            cur = next;
        }
    }

    /** Returns if the word is in the trie. */
    bool search(string word) {

        TrieNode* cur = root;
        for (int i=0; i map[word[i]];
            if(!cur){
                return false;
            }
        }
        return cur->isWord;
    }

    /** Returns if there is any word in the trie that starts with the given prefix. */
    bool startsWith(string prefix) {

        TrieNode* cur = root;
        for (int i=0; i map[prefix[i]]){
                cur = cur->map[prefix[i]];
            }else{
                return false;
            }
        }

        return (cur) ? true : false;
    }
};

