class MapSum {
private:
    struct TrieNode{
        char text;
        unordered_map map;
        int sum;
        TrieNode(){
            map = unordered_map();
            sum = 0;
        }
    };
    TrieNode* root;
    // def: 用來儲存已經建立過的 key 與 value
    unordered_map dict;
public:
    /** Initialize your data structure here. */
    MapSum() {
        root = new TrieNode();
    }

    void insert(string key, int val) {
        TrieNode* cur = root;

        for (int i=0; i map[key[i]];
            if(next == nullptr){
                next = new TrieNode();
                next->text = key[i];
            }

            // 當發現 key 已經建立過，則要將舊有的移除，由新的替代。
            if(dict.count(key)){
                next->sum = next->sum - dict[key] + val;
            }else{
                next->sum += val;
            }

            cur->map[key[i]] = next;
            cur = next;
        }
        dict[key] = val;
    }

    int sum(string prefix) {

        TrieNode* cur = root;
        for (int i=0; i map[prefix[i]];
            if(next == nullptr){
                return 0;
            }
            cur = next;
        }

        return cur->sum;
    }
};

