class WordDictionary {
private:
    struct TrieNode{
        char text;
        bool isWord;
        unordered_map map;
        TrieNode(){
            map = unordered_map();
            isWord = false;
        }
    };
    TrieNode* root;
public:
    /** Initialize your data structure here. */
    WordDictionary() {
        root = new TrieNode();
    }

    /** Adds a word into the data structure. */
    void addWord(string word) {

        TrieNode* cur = root;
        for (int i=0; i map[word[i]];
            if(next == nullptr){
                next = new TrieNode();
                next->text = word[i];
            }

            cur->map[word[i]] = next;
            cur = next;
        }
        cur->isWord = true;
    }

    /** Returns if the word is in the data structure. A word could contain the dot character '.' to represent any one letter. */
    bool search(string word) {
        return interalSearch(word, root, 0);
    }

    // def: 尋找從 index 位置的 word，在這節點是否存在
    // return: index == word sieze => 則判斷是否為字串
    bool interalSearch(string word, TrieNode* root, int i){
        if(i == word.size()){
            return root->isWord;
        }

        if(word[i] == '.'){
            for (auto& it : root->map){
                TrieNode* next = it.second;
                if(next && interalSearch(word, next, i+1)) return true;
            }
            return false;
        }else{
            TrieNode* next = root->map[word[i]];
            return next && interalSearch(word, next, i+1);
        }
    }
};

