    string repalceText(string text){

        TrieNode* cur = root;
        for (int i=0; i map[text[i]];
            cur = next;
            if(next != nullptr && next->isWord){
                return cur->word;
            }

            if(next == nullptr){
                return text;
            }
        }
        return text;
    }
public:
