        buildTrieNode(dict);
        istringstream iss(sentence);
        string s;
        string res = "";
        while (getline( iss, s, ' ' ) ) {
            string text = s.c_str();
            string replaceWord = repalceText(text);
            if(res.size() == 0){
                res = replaceWord;
            }else{
                res = res + " " + replaceWord;
            }
        }
        return res;
    }
};

