    int rob(vector& nums, int i){
        if(i = 0) return dp[i];

        return dp[i] = max(rob(nums, i-2) + nums[i], rob(nums, i-1)); 
    };
    vector dp;
public:
    int rob(vector& nums) {
        // dp[n] => 走訪前 n 個房子可以搶到多少錢。
        // dp[n] = max(dp[n-2]+nums[i], dp[n-1]);
        // dp[n] = max(拿這次的+上上一層的,這次 n 的不拿)

        dp = vector(nums.size(), -1);
        return rob(nums, nums.size()-1);
    }
};

