class Solution {
public:
    vector dailyTemperatures(vector& T) {
        vector dp(T.size());
        int n = T.size();
        dp[n-1] = 0;

        for (int i=n-2; i >=0; i--){
            int j = 1;

            // 降溫
            while(T[i] >= T[i+j]){
                if(dp[i+j] == 0){
                    j = 0; 
                    break;
                }
                j = j + dp[i+j];
            }
            dp[i] = j;
        }
        return dp;
    }
};

